import Foundation

class ItemVM: BaseViewModel<Item>, Identifiable {
    var id: Int {
        data.id
    }
    
    var name: String {
        data.name
    }
    
    var detail: String {
        data.detail
    }
    
    var imageUrl: URL? {
        URL(string: data.image)
    }
}
